import React, { Component } from "react";

export default class Lista extends Component {
  state = {
    data: [],
    loading: true,
    erro: null,
  };

  componentDidMount() {
    fetch("http://127.0.0.1:8080/time")
      .then((response) => {
        if (!response.ok) throw new Error("Erro na requisição");
        return response.json();
      })
      .then((data) => {
        this.setState({ data: data, loading: false });
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        this.setState({ erro: error.message, loading: false });
      });
  }

  editar = (idtime) => {
    alert(`Editar time com id ${idtime}`);
  };

  excluir = (idtime) => {
    if (window.confirm("Confirma exclusão?")) {
      this.setState((prev) => ({
        data: prev.data.filter((item) => item.idtime !== idtime),
      }));
    }
  };

  render() {
    const { data, loading, erro } = this.state;

    if (loading) return <p>Carregando...</p>;
    if (erro) return <p className="text-danger">Erro: {erro}</p>;
    if (!data.length)
      return (
        <p className="text-center text-danger">Nenhum time encontrado</p>
      );

    return (
      <table className="table table-striped table-hover">
        <thead>
          <tr>
            <th>Id</th>
            <th>Nome</th>
            <th>Cidade</th>
            <th>Estado</th>
            <th>Fundação</th>
            <th>Estádio</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {data.map((time) => (
            <tr key={time.idtime}>
              <td>{time.idtime}</td>
              <td>{time.nome}</td>
              <td>{time.cidade}</td>
              <td>{time.estado}</td>
              <td>{time.fundacao}</td>
              <td>{time.estadio}</td>
              <td>
                <button
                  onClick={() => this.editar(time.idtime)}
                  title="Editar"
                  style={{ border: "none", background: "none", cursor: "pointer", marginRight: 8 }}
                >
                  <img src="imgs/edit.svg" alt="Editar" width={16} height={16} />
                </button>
                <button
                  onClick={() => this.excluir(time.idtime)}
                  title="Excluir"
                  style={{ border: "none", background: "none", cursor: "pointer" }}
                >
                  <img src="imgs/x-square.svg" alt="Excluir" width={16} height={16} />
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }
}
